import * as MyPostReactionsActions from '../my-post-reactions/my-post-reactions.actions';
import { concatMap, filter, map, switchMap, take, tap } from 'rxjs/operators';
import { from, of } from 'rxjs';
import * as ReplyIdAction from '../reply-id/reply-id.actions';
import { Store } from '@ngrx/store';
import { AppState } from '../state';
import { ReplyFacade } from '../reply-id/reply.facade';
import { PostFacade } from './post.facade';
import { Update } from '@ngrx/entity';
import { Post } from '../../core/models/post/post-list-item.model';
import { updatePost } from './post.actions';
import { selectCommentPostsByPostId } from '../reply-id/reply-id.selectors';

@Injectable()
export class PostEffects {
  constructor(
    private action$: Actions,
    private postService: PostService,
    private replyFacade: ReplyFacade,
    private postFacade: PostFacade,
    private store: Store<AppState>
  ) {}

  loadPosts$ = createEffect(() =>
    this.action$.pipe(
      ofType(PostActions.loadPostsByIds),
      tap(({ payload }) =>
        this.store.dispatch(
          MyPostReactionsActions.getGetMyPostReactionsByPostIds({
            ids: payload.ids,
          })
        )
      ),
      concatMap(({ payload }) =>
        from(this.postService.loadPostsByIds(payload.ids, payload.type)).pipe(
          switchMap((postsData) => [
            PostActions.upsertPosts({ payload: postsData }),
          ])
        )
      )
    )
  );

  loadPostEffect$ = createEffect(() =>
    this.action$.pipe(
      ofType(PostActions.loadPostById),
      tap(({ id }) =>
        this.store.dispatch(
          MyPostReactionsActions.getGetMyPostReactionsByPostIds({
            ids: [id],
          })
        )
      ),
      switchMap(({ id }) => from(this.postService.loadPostById(id, 'all'))),
      filter((post) => !!post),
      map((post) => PostActions.upsertPost({ payload: post! }))
    )
  );

  switchIsChildrenCommentShow$ = createEffect(() =>
    this.action$.pipe(
      ofType(PostActions.switchIsChildrenCommentShow),
      switchMap(({ payload }) =>
        this.postFacade.getPostOnce(payload.id).pipe(
          tap((post) => {
            const isChildrenCommentShow =
              payload.open || !post.isChildrenCommentShow;
            const update: Update<Post> = {
              id: post.id,
              changes: { isChildrenCommentShow },
            };
            this.store.dispatch(updatePost({ payload: update }));
          }),
          concatMap((post) => {
            if (!post.isChildrenCommentShow) {
              return this.store
                .select(selectCommentPostsByPostId(post.id))
                .pipe(
                  take(1),
                  map((posts) =>
                    Array.isArray(posts) && posts.length === 0 ? post.id : null
                  )
                );
            } else {
              return of(null);
            }
          }),
          filter((id) => !!id),
          map((id) => ReplyIdAction.loadReplyIdsByParentPostId({ id: id! }))
        )
      )
    )
  );

  addNewCommentPost$ = createEffect(() =>
    this.action$.pipe(
      ofType(PostActions.addNewCommentPost),
      tap(({ payload }) => this.postFacade.loadPost(payload.replyId)),
      tap(({ payload }) =>
        this.postFacade.increaseReplyCount(payload.parentId)
      ),
      switchMap((props) => [
        ReplyIdAction.addReplyId({ payload: props.payload }),
        PostActions.switchIsChildrenCommentShow({
          payload: { id: props.payload.parentId, open: true },
        }),
      ])
    )
  );

  loadReplyPosts$ = createEffect(() =>
    this.action$.pipe(
      ofType(ReplyIdAction.upsertReplyIds),
      map(({ payload }) =>
        PostActions.loadPostsByIds({
          payload: { ids: payload.replyIds, reload: true, type: 'all' },
        })
      )
    )
  );
}

import { Injectable } from '@angular/core';
import { Store } from '@ngrx/store';
import { AppState } from '../state';
import { Observable } from 'rxjs';
import {
  addNewCommentPost,
  loadPostById,
  loadPostsByIds,
  switchIsChildrenCommentShow,
  updatePost,
} from './post.actions';
import { selectPostById, selectPostsByIds } from './post.selectors';
import { Post } from '../../core/models/post/post-list-item.model';
import { takeValueOnce } from '../../core/rxjs-custom/operators';
import { Update } from '@ngrx/entity';
import { AddReplyIdProps } from '../reply-id/reply-id.actions';

@Injectable({
  providedIn: 'root',
})
export class PostFacade {
  constructor(private store: Store<AppState>) {}

  loadPost(id: string) {
    this.store.dispatch(loadPostById({ id }));
  }

  loadPosts(ids: string[], type?: 'public' | 'all') {
    this.store.dispatch(loadPostsByIds({ payload: { ids, type } }));
  }

  getPost(id: string): Observable<Post | undefined> {
    return this.store.select(selectPostById(id));
  }

  getPosts(ids: string[]): Observable<Post[]> {
    return this.store.select(selectPostsByIds(ids));
  }

  getPostOnce(id: string): Observable<Post> {
    return this.getPost(id).pipe(takeValueOnce) as Observable<Post>;
  }

  fetchPost(id: string): Observable<Post> {
    this.loadPost(id);
    return this.getPostOnce(id);
  }

  updatePost(update: Update<Post>) {
    this.store.dispatch(updatePost({ payload: update }));
  }

  increaseReplyCount(id: string) {
    this.getPostOnce(id).subscribe((post) => {
      const update: Update<Post> = {
        id: id,
        changes: { repliesCount: post.repliesCount + 1 },
      };

      this.updatePost(update);
    });
  }

  switchIsChildrenCommentShow(id: string, open?: boolean) {
    this.store.dispatch(switchIsChildrenCommentShow({ payload: { id, open } }));
  }

  addNewCommentPost(props: AddReplyIdProps) {
    this.store.dispatch(addNewCommentPost({ payload: props }));
  }

  switchVisibility(postId: string) {
    this.getPostOnce(postId).subscribe((post) => {
      const update: Update<Post> = {
        id: postId,
        changes: { hidden: !post.hidden },
      };

      this.updatePost(update);
    });
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { forkJoin, from, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { environment } from '../../../environments/environment';
import { LinkService } from '../../shared/services/link.service';
import { SubsocialApiService } from '../../shared/services/subsocial-api.service';
import { PostId } from '@subsocial/types/substrate/interfaces';
import { PostWithAllDetails } from '@subsocial/api/flat-subsocial/dto';
import { METHODS, PALLETS } from '../../core/constants/query.const';
import { flatReaction } from '../../core/mapper/flatten.map';
import { ConvertService } from '../../shared/services/convert.service';
import { StoreService } from '../../state/store.service';
import { Post } from '../../core/models/post/post-list-item.model';
import { mapPostDTOToPost } from '../../core/mapper/post.map';

@Injectable({
  providedIn: 'root',
})
export class PostService {
  spaceIds = environment.recommendedSpaceIds;
  constructor(
    private http: HttpClient,
    private link: LinkService,
    private api: SubsocialApiService,
    private convert: ConvertService,
    private storeService: StoreService
  ) {}

  getSuggestedPostsIds() {
    return this.getPostIdsBySpaceIds(this.spaceIds);
  }

  getPostIdsBySpaceIds(spaceIds: string[]): Observable<string[]> {
    const allIds$: Observable<PostId[]>[] = [];
    spaceIds.forEach((id) => {
      allIds$.push(
        from(
          this.api.api.subsocial.substrate.postIdsBySpaceId(
            this.convert.convertToBN(id)
          )
        )
      );
    });

    return forkJoin(allIds$).pipe(
      map((postIds) => {
        return postIds
          .flat()
          .sort((a, b) => b.sub(a).toNumber())
          .map((id) => id.toString());
      })
    );
  }

  async getPostIdsBySpaceId(id: string) {
    const ids = await this.api.api.subsocial.substrate.postIdsBySpaceId(
      this.convert.convertToBN(id)
    );
    return ids.sort((a, b) => b.sub(a).toNumber()).map((id) => id.toString());
  }

  async loadPostsByIds(
    ids: string[],
    type: 'public' | 'all' = 'public'
  ): Promise<Post[]> {
    const postsData =
      type === 'public'
        ? await this.findPublicPostsWithAllDetails(ids)
        : await this.findPostsWithAllDetails(ids);

    return this.mapPostsDTO(postsData);
  }

  async loadPostById(
    id: string,
    type: 'public' | 'all' = 'public'
  ): Promise<Post | undefined> {
    const postsData =
      type === 'public'
        ? await this.findPublicPostsWithAllDetails([id])
        : await this.findPostsWithAllDetails([id]);

    if (postsData[0]) {
      const { post, space, owner } = postsData[0];

      return mapPostDTOToPost(post, space, owner?.content);
    }

    return undefined;
  }

  async findPublicPostsWithAllDetails(ids: string[]) {
    return await this.api.api.findPublicPostsWithAllDetails(ids);
  }

  async findPostsWithAllDetails(ids: string[]) {
    return await this.api.api.findPostsWithAllDetails({
      ids: this.convert.convertToBNArray(ids),
    });
  }

  async getReactionIdsByPostId(id: string) {
    return await this.api.getSubstrateIdsById({
      pallet: PALLETS.reactions,
      method: METHODS.reactionIdsByPostId,
      id,
    });
  }

  async getReactionsByIds(ids: string[]) {
    const reactions = await this.api.api.subsocial.substrate.findReactions(
      this.convert.convertToBNArray(ids)
    );

    return reactions.map((reaction) => flatReaction(reaction));
  }

  public mapPostsDTO(postsAllData: PostWithAllDetails[]): Post[] {
    const posts: Post[] = [];

    postsAllData.forEach((postAllData) => {
      const { post, space, owner } = postAllData;

      if (post?.content) {
        posts.push(mapPostDTOToPost(post, space, owner?.content));
      }
    });

    return posts;
  }
}

