import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import * as ReplyIdAction from './reply-id.actions';
import { map, switchMap, take } from 'rxjs/operators';
import { from } from 'rxjs';
import { CommentService } from '../../shared/services/comment.service';
import { PostFacade } from '../post/post.facade';
import { ReplyFacade } from './reply.facade';
import { Update } from '@ngrx/entity';
import { ReplyIds } from '../../core/types/reply-id.type';

@Injectable()
export class ReplyIdEffects {
  constructor(
    private action$: Actions,
    private postFacade: PostFacade,
    private replyFacade: ReplyFacade,
    private commentService: CommentService
  ) {}

  addReplyId$ = createEffect(() =>
    this.action$.pipe(
      ofType(ReplyIdAction.addReplyId),
      switchMap(({ payload }) =>
        this.replyFacade.getReplyIdsForPost(payload.parentId).pipe(
          take(1),
          map((replyIds) => {
            let update: Update<ReplyIds>;

            if (replyIds) {
              update = {
                id: payload.parentId,
                changes: { replyIds: [payload.replyId, ...replyIds!.replyIds] },
              };
            } else {
              update = {
                id: payload.parentId,
                changes: { replyIds: [payload.replyId] },
              };
            }
            return ReplyIdAction.updateReplyIds({ payload: update });
          })
        )
      )
    )
  );

  loadReplyIds$ = createEffect(() =>
    this.action$.pipe(
      ofType(ReplyIdAction.loadReplyIdsByParentPostId),
      switchMap(({ id }) =>
        from(this.commentService.getPostReplyId(id)).pipe(
          map((replyIds) => ReplyIdAction.upsertReplyIds({ payload: replyIds }))
        )
      )
    )
  );
}

import { Injectable } from '@angular/core';
import { environment } from '../../../environments/environment';
import { HttpClient } from '@angular/common/http';
import { catchError } from 'rxjs/operators';
import { of } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class SessionService {
  constructor(private http: HttpClient) {}

  offchainUrl = environment.offchainUrl;

  async addSessionKey(sessionCall: any) {
    await this.http
      .post(
        this.offchainUrl + '/v1/offchain/accounts/setSessionKey',
        sessionCall
      )
      .pipe(
        catchError((err) => {
          console.error(
            'Failed to add a session key to main account:',
            sessionCall.account,
            'res.status:',
            err.status
          );
          return of(null);
        })
      )
      .toPromise();
  }

  async getNonce(account: string) {
    return await this.http
      .get<string>(
        this.offchainUrl + `/v1/offchain/accounts/getNonce?account=${account}`
      )
      .toPromise();
  }

  async getSessionKey(account: string) {
    return await this.http
      .get(
        this.offchainUrl +
          `/v1/offchain/accounts/getSessionKey?account=${account}`,
        {}
      )
      .pipe(
        catchError((err) => {
          console.error(
            `Failed to check if a session key is known on offchain: ${account}`,
            err
          );
          return of(null);
        })
      )
      .toPromise();
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class SideNavService {
  private isShowAccountSideNavSource = new BehaviorSubject<boolean>(false);
  public isShowAccountSideNav$: Observable<boolean> =
    this.isShowAccountSideNavSource.asObservable();

  constructor() {}

  openAccountSideNav() {
    this.isShowAccountSideNavSource.next(true);
  }

  closeAccountSideNav() {
    this.isShowAccountSideNavSource.next(false);
  }
}

import { Injectable } from '@angular/core';
import { SignInModalDialogComponent } from '../sign-in-modal-dialog/sign-in-modal-dialog.component';
import { AccountService } from '../../../shared/services/account.service';
import { MatDialog } from '@angular/material/dialog';
import { filter } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class SignInModalService {
  constructor(
    private dialog: MatDialog,
    private accountService: AccountService
  ) {}

  async openModal(isTx?: boolean) {
    const { accounts, status } = await this.accountService.getSignInData();

    const dialogRef = this.dialog.open(SignInModalDialogComponent, {
      maxWidth: '430px',
      data: {
        status,
        accounts,
        isTx,
      },
    });

    dialogRef
      .afterClosed()
      .pipe(filter((data) => !!data))
      .subscribe((account) => this.accountService.setCurrentAccount(account!));
  }
}

import { Injectable } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import {
  SimpleSnackBarComponent,
  SimpleSnackBarOption,
} from '../components/simple-snack-bar/simple-snack-bar.component';

@Injectable({
  providedIn: 'root',
})
export class SnackBarService {
  constructor(readonly snackBar: MatSnackBar) {}

  public openSimpleSnackBar(options: SimpleSnackBarOption) {
    return this.snackBar.openFromComponent(SimpleSnackBarComponent, {
