import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import * as SpaceActions from './space.actions';
import { filter, switchMap } from 'rxjs/operators';
import { SpaceService } from '../../space/services/space.service';
import { from } from 'rxjs';

@Injectable()
export class SpaceEffects {
  constructor(private action$: Actions, private spaceService: SpaceService) {}

  loadSpaces$ = createEffect(() =>
    this.action$.pipe(
      ofType(SpaceActions.loadSpacesByIds),
      switchMap(({ payload }) =>
        from(this.spaceService.loadSpacesByIds(payload.ids, payload.type)).pipe(
          switchMap((spaceData) => [
            SpaceActions.upsertSpaces({
              payload: spaceData,
            }),
          ])
        )
      )
    )
  );

  loadSpace$ = createEffect(() =>
    this.action$.pipe(
      ofType(SpaceActions.loadSpaceById),
      switchMap(({ id }) =>
        from(this.spaceService.loadSpaceById(id)).pipe(
          filter((space) => space !== undefined),
          switchMap((space) => [SpaceActions.upsertSpace({ payload: space })])
        )
      )
    )
  );
}

import { Injectable } from '@angular/core';
import { Store } from '@ngrx/store';
import { AppState } from '../state';
import { loadSpaceById, loadSpacesByIds, updateSpace } from './space.actions';
import { selectSpaceById, selectSpacesByIds } from './space.selectors';
import { Observable } from 'rxjs';
import { Space } from './space.state';
import { takeValueOnce } from '../../core/rxjs-custom/operators';
import { Update } from '@ngrx/entity';

@Injectable({
  providedIn: 'root',
})
export class SpaceFacade {
  constructor(private store: Store<AppState>) {}

  loadSpace(id: string) {
    this.store.dispatch(loadSpaceById({ id }));
  }

  loadSpaces(ids: string[], type?: 'public' | 'all') {
    this.store.dispatch(loadSpacesByIds({ payload: { ids, type } }));
  }

  getSpace(id: string): Observable<Space | undefined> {
    return this.store.select(selectSpaceById(id));
  }

  getSpaces(ids: string[]): Observable<Space[]> {
    return this.store.select(selectSpacesByIds(ids));
  }

  fetchSpaces(ids: string[]): Observable<Space[]> {
    this.loadSpaces(ids);
    return this.getSpacesOnce(ids);
  }

  getSpaceOnce(id: string): Observable<Space | undefined> {
    return this.store.select(selectSpaceById(id)).pipe(takeValueOnce);
  }

  getSpacesOnce(ids: string[]): Observable<Space[]> {
    return this.store.select(selectSpacesByIds(ids)).pipe(takeValueOnce);
  }

  updateSpace(update: Update<Space>) {
    this.store.dispatch(updateSpace({ payload: update }));
  }

  switchVisibility(spaceId: string) {
    this.getSpaceOnce(spaceId).subscribe((space) => {
      const update: Update<Space> = {
        id: spaceId,
        changes: { isHidden: !space?.isHidden },
      };

      this.updateSpace(update);
    });
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { SubsocialApiService } from '../../shared/services/subsocial-api.service';
import { SpaceStruct } from '@subsocial/api/flat-subsocial/flatteners';
import { SpaceId } from '@subsocial/types/substrate/interfaces';
import { Store } from '@ngrx/store';
import { AppState } from '../../state/state';
import {
  selectSpaceById,
  selectSpaceEntitiesByIds,
} from '../../state/space/space.selectors';
import {
  loadSpacesByIds,
  loadSpaceById,
  loadSpaceSuccess,
} from '../../state/space/space.actions';
import { METHODS, PALLETS } from '../../core/constants/query.const';
import { ConvertService } from '../../shared/services/convert.service';
import { StoreService } from '../../state/store.service';
import { SpaceListItemData } from '../../core/models/space/space-list-item.model';
import { BehaviorSubject } from 'rxjs';
import { AccountService } from '../../shared/services/account.service';
import { mapSpaceDTOToSpace } from '../../core/mapper/space.map';
import { Space } from '../../state/space/space.state';

@Injectable({
  providedIn: 'root',
})
export class SpaceService {
  constructor(
    private http: HttpClient,
    private apiService: SubsocialApiService,
    private store: Store<AppState>,
    private convert: ConvertService,
    private storeService: StoreService,
    private accountService: AccountService
  ) {}

  private myOwnSpaceIdsSource = new BehaviorSubject<string[] | null>(null);
  myOwnSpaceIds$ = this.myOwnSpaceIdsSource.asObservable();

  public async getSpacesByIds(ids: string[]): Promise<SpaceStruct[]> {
    return await this.apiService.api.findSpaceStructs(ids);
  }

  public async getMyOwnSpaceIds() {
    const accountId = this.accountService.getCurrentAccountId();
    if (accountId) {
      const ids = await this.getSpaceIdsByAccount(accountId);

      if (ids?.length > 0) {
        this.myOwnSpaceIdsSource.next(ids);
        return;
      }
    }

    this.myOwnSpaceIdsSource.next(null);
  }

  public async loadSpacesByIds(
    ids: string[],
    type: 'public' | 'all' = 'public'
  ): Promise<Space[]> {
    const spaceData = await this.apiService.api.findPublicSpaces(ids);

    if (type === 'public') {
      const unlistedSpaces = await this.apiService.api.findUnlistedSpaces(ids);
      spaceData.push(...unlistedSpaces);
    }

    return spaceData.map((data) => mapSpaceDTOToSpace(data));
  }

  async getSpaceIdByHandle(handle: string): Promise<string | undefined> {
    const res: SpaceId | undefined =
      await this.apiService.api.subsocial.substrate.getSpaceIdByHandle(
        handle.toLowerCase()
      );

    return res?.toString();
  }

  async loadSpaceById(id: string) {
    const spaceData = await this.apiService.api.findSpace({
      id: this.convert.convertToBN(id),
    });

    return spaceData ? mapSpaceDTOToSpace(spaceData) : undefined;
  }

  async getFollowersIdsBySpaceId(id: string) {
    return await this.apiService.getSubstrateIdsById({
      pallet: PALLETS.spaceFollows,
      method: METHODS.spaceFollowers,
      id,
    });
  }

  async reloadSpaceById(id: string) {
    return (await this.storeService.reloadEntity(
      selectSpaceById,
      loadSpaceById,
      loadSpaceSuccess,
      id,
      { id }
    )) as SpaceListItemData;
  }

  async getOrLoadSpacesByIds(ids: string[] | undefined) {
    const spaceEntityData = await this.storeService.getOrLoadEntities(
      selectSpaceEntitiesByIds,
      loadSpacesByIds,
      loadSpaceSuccess,
      ids,
      { payload: { ids } }
    );

    return Object.values(spaceEntityData) as SpaceListItemData[];
  }

  async getSpaceIdsByAccount(id: string) {
    const spaceId =
      await this.apiService.api.subsocial.substrate.spaceIdsByOwner(id);
    return spaceId.map((id) => id.toString());
  }
}

import { Inject, Injectable } from '@angular/core';

export const STORAGE_KEYS = {
  accountId: 'accountId',
  lang: 'lang',
};

@Injectable({
  providedIn: 'root',
})
export class StorageService {
  constructor() {}

  setCurrentAccountId(id: string) {
    localStorage.setItem(STORAGE_KEYS.accountId, id);
  }

  getAccountId() {
    return localStorage.getItem(STORAGE_KEYS.accountId);
  }

  removeAccountId() {
    localStorage.removeItem(STORAGE_KEYS.accountId);
  }

  setLang(lang: string) {
    this.set(STORAGE_KEYS.lang, lang);
  }

  getLang() {
    return this.get(STORAGE_KEYS.lang);
  }

  get(key: string) {
    const value = localStorage.getItem(key);
    return value ? JSON.parse(value) : undefined;
  }

  set(key: string, obj: any) {
    localStorage.setItem(key, JSON.stringify(obj));
  }
}

import { Injectable } from '@angular/core';
import { ActionCreator, Store } from '@ngrx/store';
import { AppState } from './state';
import { Actions, ofType } from '@ngrx/effects';
import { mergeMap, take } from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class StoreService {
  constructor(private store: Store<AppState>, private action$: Actions) {}

  public async getValueFromStore(
    selector: Function,
    action: ActionCreator,
    getProps: any
  ) {
    return await this.action$
      .pipe(
        ofType(action),
        mergeMap((_) => this.store.select(selector(getProps))),
        take(1)
      )
      .toPromise();
  }

  public async getOrLoadEntities(
    selector: Function,
    loadAction: Function,
    successAction: ActionCreator,
    getProps: any,
    loadProps: any
  ) {
    const entitiesFromStore = await this.store
      .select(selector(getProps))
      .pipe(take(1))
      .toPromise();

    if (
      !entitiesFromStore ||
      entitiesFromStore?.length === 0 ||
      Object.keys(entitiesFromStore).length === 0
    ) {
      this.store.dispatch(loadAction(loadProps));

      return await this.getValueFromStore(selector, successAction, getProps);
    }

    return entitiesFromStore;
  }

  public async reloadEntity(
    selector: Function,
    loadAction: Function,
    successAction: ActionCreator,
    getProps: any,
    loadProps: any
  ) {
    this.store.dispatch(loadAction(loadProps));
    return await this.getValueFromStore(selector, successAction, getProps);
  }
}

import { Inject, Injectable, PLATFORM_ID } from '@angular/core';
import { newFlatSubsocialApi, SubsocialSubstrateApi } from '@subsocial/api';
import { environment } from '../../../environments/environment';
import { FlatSubsocialApi } from '@subsocial/api/flat-subsocial';
import { MatSnackBar } from '@angular/material/snack-bar';
import { AnyId } from '@subsocial/api/flat-subsocial/dto';
import { SnackBarService } from './snack-bar.service';
import { ConvertService } from './convert.service';

type FetchSubstrateProps = {
  pallet: string;
  method: string;
  id: AnyId;
};

type FetchSubstrateMultiProps = {
  pallet: string;
  method: string;
  tuples: string[][];
};

@Injectable({
  providedIn: 'root',
})
export class SubsocialApiService {
  constructor(
    @Inject(PLATFORM_ID) private platformId: Object,
    private _snackBar: MatSnackBar,
    private snackBarService: SnackBarService,
    private convert: ConvertService
  ) {}
  public api: FlatSubsocialApi;

  public get substrate() {
    return this.api.subsocial.substrate;
  }

  async initSubsocialApi() {
    const snackBar = this.snackBarService.openSimpleSnackBar({
      message: 'Connecting to network...',
      iconName: 'info-icon',
      isShowCloseButton: true,
    });

    const { substrateNodeUrl, offchainUrl, ipfsNodeUrl } = environment;
    this.api = await newFlatSubsocialApi({
      offchainUrl,
      substrateNodeUrl,
      ipfsNodeUrl,
      useServer: {
        httpRequestMethod: 'get',
      },
    });

    snackBar.dismiss();
  }

  async getSubstrateIdsById(props: FetchSubstrateProps) {
    const readyApi = await this.api.subsocial.substrate.api;
    const { pallet, method, id } = props;
    const ids = await readyApi.query[pallet][method](id);
    return (ids.toJSON() as Array<any>).map((id) => id.toString());
  }

  async getSubstrateIdsByMulti(props: FetchSubstrateMultiProps) {
    const readyApi = await this.api.subsocial.substrate.api;
    const { pallet, method, tuples } = props;
    const ids = await readyApi.query[pallet][method].multi(tuples);
    return ids;
  }

  async getContentArray(ids: string[]) {
    return await this.api.subsocial.ipfs.getContentArray(ids);
  }
}

import { Injectable } from '@angular/core';
import { SubsocialApiService } from './subsocial-api.service';
import { ExtrinsicProps } from '../../core/base-component/base-tx.component';
import { SnackBarService } from './snack-bar.service';
import { MESSAGES } from '../../core/constants/messages';
import { SimpleSnackBarComponent } from '../components/simple-snack-bar/simple-snack-bar.component';
import { MatSnackBarRef } from '@angular/material/snack-bar';
import { CommonContent } from '@subsocial/types';
import { environment } from '../../../environments/environment';
import { HttpClient } from '@angular/common/http';

export type IpfsContentProps = {
  body?: string;
};

@Injectable({
  providedIn: 'root',
})
export class TransactionService {
  constructor(
    private api: SubsocialApiService,
    private snackBarService: SnackBarService,
    private http: HttpClient
  ) {}

  waitMessageRef: MatSnackBarRef<SimpleSnackBarComponent>;

  async getExtrinsic({ pallet, method, params }: ExtrinsicProps) {
    const api = await this.api.api.subsocial.substrate.api;
    if (!api.tx[pallet]) {
      throw new Error(`Unable to find api.tx.${pallet}`);
    } else if (!api.tx[pallet][method]) {
      throw new Error(`Unable to find api.tx.${pallet}.${method}`);
    }

    return api.tx[pallet][method](...params);
  }

  async saveIpfsContent(content: CommonContent) {
    try {
      const cid = await this.api.api.subsocial.ipfs.saveContent(content);
      if (cid) {
        return cid;
      } else {
        console.error('Save to IPFS returned an undefined CID');
      }
    } catch (err) {
      console.error(`Failed to build tx params. ${err}`);
    }
    return undefined;
  }

  async saveFile(file: File) {
    const { addFileUrl } = environment;

    const formData = new FormData();
    formData.append('file', file);
    return this.http.post(addFileUrl, formData).toPromise();
  }

  async removeContent(cid: string) {
    try {
      await this.api.api.subsocial.ipfs.removeContent(cid);
    } catch (e) {
      console.error('Content not removed', e);
    }
  }

  showSuccessMessage(message: string) {
    this.snackBarService.openSimpleSnackBar({
      message,
      isShowCloseButton: true,
      duration: 2000,
    });
  }

  showErrorMessage(message: string) {
    this.snackBarService.showErrorMessage(message);
  }

  showWaitMessage() {
    this.waitMessageRef = this.snackBarService.openSimpleSnackBar({
      message: MESSAGES.WAITING_TX_COMPLETE,
      isWait: true,
    });
  }

  closeWaitMessage() {
    this.waitMessageRef.dismiss();
  }
}

import { Injectable } from '@angular/core';
import { makeStateKey, TransferState } from '@angular/platform-browser';

@Injectable({
  providedIn: 'root',
})
export class TransferStateService {
  constructor(private transferState: TransferState) {}

  public set<T>(key: string, object: T) {
    const stateKey = makeStateKey<T>(key);
    this.transferState.set<T>(stateKey, object);
  }

  public get<T>(key: string): T {
    const stateKey = makeStateKey<T>(key);
    return this.transferState.get<T>(stateKey, <any>null);
  }

  public remove<T>(key: string) {
    const stateKey = makeStateKey<T>(key);
    this.transferState.remove<T>(stateKey);
  }
}

