import { animate, style, transition, trigger } from '@angular/animations';

@Component({
  template: '',
  animations: [
    trigger('errorsAnimations', [
      transition(':enter', [
        style({ top: '-20px', opacity: 0 }),
        animate('150ms', style({ top: '0', opacity: 1 })),
      ]),
      transition(':leave', [
        style({ top: '0', opacity: 1 }),
        animate('150ms', style({ top: '-20px', opacity: 0 })),
      ]),
    ]),
  ],
})
export class BaseControlValueAccessorComponent implements ControlValueAccessor {
  @Input() error: string = '';
  @Output() blur = new EventEmitter();

  onChange: any = () => {};
  onTouch: any = () => {};
  protected _value: string | string[] = '';

  set value(val: string | string[]) {
    this._value = val;
    this.onChange(this._value);
    this.onTouch(this._value);
  }

  get value(): string | string[] {
    return this._value;
  }

  constructor() {}

  registerOnChange(fn: any): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouch = fn;
  }

  writeValue(obj: any): void {
    this.value = obj;
    this.onBlur();
  }

  onBlur() {
    this.onTouch();
    this.blur.emit();
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  Input,
  OnDestroy,
} from '@angular/core';
import { ScrollProps } from '../classes/scroll-props.class';
import { BehaviorSubject, Observable, Subject } from 'rxjs';
import { HasId } from '@subsocial/api/flat-subsocial/flatteners';
import { AppState } from '../../state/state';
import { Store } from '@ngrx/store';
import { delay, filter, map, switchMap, tap } from 'rxjs/operators';
import { Actions } from '@ngrx/effects';

@Component({
  template: '',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BaseInfinityScrollComponent<T extends HasId> implements OnDestroy {
  @Input() ids: string[] | null;
  @Input() type: 'public' | 'all' = 'public';
  @Input() showHiddenContent: boolean | null;

  public items$: Observable<T[]>;
  public scrollDistance = 1;
  public scrollProps = new ScrollProps(20);
  public isBlockInfinityScroll = false;
  public isLoading: boolean;

  private idsSet = new Set<string>();

  private scrollDownEventSource = new BehaviorSubject<ScrollProps>(
    this.scrollProps
  );

  scrollDownEvent$ = this.scrollDownEventSource.asObservable();

  private unsubscribe$: Subject<void> = new Subject();

  constructor(
    public store: Store<AppState>,
    public cd: ChangeDetectorRef,
    public action$: Actions
  ) {}

  onScrollDown() {
    if (!this.isBlockInfinityScroll) {
      this.scrollProps.next();
      this.scrollDownEventSource.next(this.scrollProps);
    }
  }

  getScrollableData(dispatchAction: Function, selector: Function) {
    if (!this.ids || this.ids?.length === 0) {
      return;
    }

    this.scrollProps.max = this.ids.length;

    this.items$ = this.scrollDownEvent$.pipe(
      filter((props) => !props.isFinish),
      tap((_) => this.loadStart()),
      map((props) => this.sliceIds(this.ids!, props)),
      tap((slicedIds) =>
        this.store.dispatch(
          dispatchAction({ payload: { ids: slicedIds, type: this.type } })
        )
      ),
      map((ids) => this.getUniqueIds(ids)),
      switchMap((ids) => this.store.select(selector(ids)).pipe(delay(300))),
      filter((items) => items.length > 0),
      tap((_) => this.loadFinish())
    );
  }

  getUniqueIds(ids: string[]) {
    ids.forEach((id) => this.idsSet.add(id));
    return Array.from(this.idsSet);
  }

  trackById(index: number, item: T) {
    return item.id;
  }

  private sliceIds(arr: Array<string>, props: ScrollProps): string[] {
    return arr.slice(props.startIndex, props.endIndex).map((e) => e.toString());
  }

  private loadStart() {
    this.isLoading = true;
    this.isBlockInfinityScroll = true;
    this.cd.markForCheck();
  }

  private loadFinish() {
    this.isLoading = false;
    this.isBlockInfinityScroll = false;
    this.cd.markForCheck();
  }

  ngOnDestroy(): void {
    this.unsubscribe$?.next();
    this.unsubscribe$?.complete();
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  Inject,
} from '@angular/core';
import { TransactionService } from '../../shared/services/transaction.service';
import { AccountService } from '../../shared/services/account.service';
import { skipWhile, take } from 'rxjs/operators';
import { SubmittableResult } from '@polkadot/api';
import { CommonContent, IpfsCid } from '@subsocial/types';
import { SignInModalService } from '../../ui-lib/modal-dialogs/services/sign-in-modal.service';

export type ExtrinsicProps = {
  pallet: string;
  method: string;
  params: string[] | any[];
};

@Component({
  template: '',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export abstract class BaseTxComponent {
  extrinsic: any;
  signer: any;
  accountId: string | undefined;
  txUnsub: Function;
  isSending: boolean = false;
  isAuthRequired: boolean = true;
  isFreeTx: boolean = false;
  contentCid: IpfsCid | undefined;

  abstract onSuccess(result: SubmittableResult): void;
  abstract onFailed(result: SubmittableResult | null): void;
  abstract validate(): boolean;

  protected constructor(
    public transactionService: TransactionService,
    public accountService: AccountService,
    public signIn: SignInModalService,
    public cd: ChangeDetectorRef
  ) {}

  protected async initExtrinsic(props: ExtrinsicProps) {
    this.accountService.signer$
      .pipe(
        skipWhile((sinner) => sinner === null),
        take(1)
      )
      .subscribe((sinner) => (this.signer = sinner));

    this.accountService.currentAccount$
      .pipe(skipWhile((account) => account === null))
      .subscribe((account) => (this.accountId = account?.id));

    this.extrinsic = await this.transactionService.getExtrinsic(props);
  }

  doOnSuccess(result: SubmittableResult) {
    this.onSuccess(result);
  }

  async doOnFailed(result: SubmittableResult | null) {
    console.error(result);
    this.onFailed(result);
    await this.clear();
    this.setIsSending(false);
  }

  async sentTransaction() {
    this.unsubscribe();

    if (!this.validate()) {
      console.warn('Cannot send a tx because validate() returned false');
      return;
    }

    const txType = this.accountId ? 'signed' : 'unsigned';
    console.debug(`Sending ${txType} tx...`);

    this.setIsSending(true);

    if (txType === 'unsigned') {
      if (this.isAuthRequired && !this.isFreeTx) {
        await this.signIn.openModal(true);
        return this.setIsSending(false);
      }
      await this.sendUnsignedTx();
    } else {
      await this.sendSignedTx();
    }
  }

  async onSuccessHandler(result: SubmittableResult) {
    if (!result || !result.status) {
      return;
    }

    const { status } = result;
    if (status.isFinalized || status.isInBlock) {
      this.setIsSending(false);
      this.unsubscribe();

      const blockHash = status.isFinalized
        ? status.asFinalized
        : status.asInBlock;

      console.warn(` Tx finalized. Block hash: ${blockHash.toString()}`);

      result.events
        .filter(({ event: { section } }): boolean => section === 'system')
        .forEach(({ event: { method } }): void => {
          if (method === 'ExtrinsicSuccess') {
            this.doOnSuccess(result);
          } else if (method === 'ExtrinsicFailed') {
            this.doOnFailed(result);
          }
        });
    } else if (result.isError) {
      await this.doOnFailed(result);
    } else {
      console.warn(` Current tx status: ${status.type}`);
    }
  }

  async onFailedHandler(err: Error) {
    if (err) {
      const errMsg = `Tx failed: ${err.toString()}`;
      console.warn(` ${errMsg}`);
      this.transactionService.showErrorMessage(errMsg);
    }

    await this.doOnFailed(null);
  }

  async sendSignedTx() {
    if (!this.accountId) {
      throw new Error('No account id provided');
    }

    if (!this.signer) {
      throw new Error('No signer provided');
    }

    try {
      const tx = await this.extrinsic.signAsync(this.accountId, {
        signer: this.signer,
      });
      this.txUnsub = await tx.send(this.onSuccessHandler.bind(this));
      this.transactionService.showWaitMessage();
    } catch (err: any) {
      await this.onFailedHandler(err);
      console.error(err);
    }
  }

  async sendUnsignedTx() {
    try {
      this.txUnsub = await this.extrinsic.send(
        this.onSuccessHandler.bind(this)
      );
      this.transactionService.showWaitMessage();
    } catch (err: any) {
      await this.onFailedHandler(err);
    }
  }

  async saveContent(content: CommonContent) {
    return await this.transactionService.saveIpfsContent(content);
  }

  setIsSending(value: boolean) {
    this.isSending = value;
    this.cd.markForCheck();
  }

  setIsAuthRequired(value: boolean) {
    this.isAuthRequired = value;
  }

  setIsFreeTx(value: boolean) {
    this.isFreeTx = value;
  }

  getNewIdsFromEvent(result: SubmittableResult) {
    const newIds: string[] = [];

    result.events.find((event) => {
      const {
        event: { data, method },
      } = event;
      if (method.indexOf('Created') >= 0) {
        const [, /* owner */ ...ids] = data.toArray();
        newIds.push(...(ids.map((id) => id.toString()) as string[]));
        return true;
      }
      return false;
    });

    return newIds;
  }

  async clear() {
    this.contentCid
      ? await this.transactionService.removeContent(this.contentCid as string)
      : null;
  }

  unsubscribe() {
    if (this.txUnsub) {
      this.transactionService.closeWaitMessage();
      this.txUnsub();
    }
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Inject,
  Input,
  Output,
} from '@angular/core';
import { BaseTxComponent } from './base-tx.component';
import { TransactionService } from '../../shared/services/transaction.service';
import { AccountService } from '../../shared/services/account.service';
import { SubmittableResult } from '@polkadot/api';
import { METHODS, PALLETS } from '../constants/query.const';
import { PostService } from '../../post/services/post.service';
import { SpaceService } from '../../space/services/space.service';
import { AppState } from '../../state/state';
import { Store } from '@ngrx/store';
import { loadPostById } from '../../state/post/post.actions';
import { VisibilityService } from '../../shared/services/visibility.service';
import { SignInModalService } from '../../ui-lib/modal-dialogs/services/sign-in-modal.service';

type VisibleOperationType = 'post' | 'space';

@Component({
  template: '',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BaseVisibleTxComponent extends BaseTxComponent {
  @Output() switchHidden = new EventEmitter();

  visibleOperationType: VisibleOperationType;
  entityId: string;

  get isPost() {
    return this.visibleOperationType === 'post';
  }

  constructor(
    public transaction: TransactionService,
    public account: AccountService,
    public cd: ChangeDetectorRef,
    public signIn: SignInModalService,
    public visibility: VisibilityService
  ) {
    super(transaction, account, signIn, cd);
  }

  onFailed(result: SubmittableResult | null): void {}

  async onSuccess(result: SubmittableResult) {
    if (this.visibleOperationType === 'post') {
      this.visibility.switchPostVisibility(this.entityId);
    } else if (this.visibleOperationType === 'space') {
      this.visibility.switchSpaceVisibility(this.entityId);
    }

    this.switchHidden.emit();
  }

  validate(): boolean {
    return true;
  }

  async hide() {
    await this.setupInitExtrinsic(true);
    await this.sentTransaction();
  }

  async show() {
    await this.setupInitExtrinsic(false);
    await this.sentTransaction();
  }

  private async setupInitExtrinsic(hidden: boolean) {
    const pallet = this.isPost ? PALLETS.posts : PALLETS.spaces;
    const method = this.isPost ? METHODS.updatePost : METHODS.updateSpace;

    let update: any;

    if (this.isPost) {
      update = {
        spaceId: null,
        content: null,
        hidden,
      };
    } else {
      update = {
        handle: null,
        content: null,
        hidden,
        permissions: null,
      };
    }

    const params = [this.entityId, update];

    await this.initExtrinsic({ pallet, params, method });
  }
}

