import { Injectable } from '@angular/core';
import { SubsocialApiService } from './subsocial-api.service';
import { CanHaveContent } from '@subsocial/api/flat-subsocial/flatteners';

@Injectable({
  providedIn: 'root',
})
export class ContentService {
  constructor(private apiService: SubsocialApiService) {}

  public async getEntityContentArray(entity: Array<CanHaveContent>) {
    const ids: string[] = entity
      .filter((e) => e.contentId !== undefined)
      .map((e) => e.contentId!);
    return await this.apiService.getContentArray(ids);
  }
}

import { Injectable } from '@angular/core';
import * as BN from 'bn.js';

@Injectable({
  providedIn: 'root',
})
export class ConvertService {
  constructor() {}

  public convertToBN(value: string) {
    return new BN(value);
  }

  public convertToBNArray(array: string[]) {
    return array.map((el) => this.convertToBN(el));
  }

  public convertBN(value: BN) {
    return value.toString();
  }

  public convertBNs(bns: Array<BN>) {
    return bns.map((bn) => bn.toString());
  }
}

import { Injectable } from '@angular/core';
import * as dayjs from 'dayjs';
import * as relativeTime from 'dayjs/plugin/relativeTime';
import * as updateLocale from 'dayjs/plugin/updateLocale';

@Injectable({
  providedIn: 'root',
})
export class DateService {
  constructor() {
    dayjs.extend(relativeTime);
    dayjs.extend(updateLocale);
    dayjs.updateLocale('en', {
      relativeTime: {
        future: 'in %s',
        past: '%s ago',
        s: 'a few seconds',
        m: 'a minute',
        mm: '%dm',
        h: 'an hour',
        hh: '%dh',
        d: 'a day',
        dd: '%dd',
        M: 'a month',
        MM: '%d months',
        y: 'a year',
        yy: '%d years',
      },
    });
  }

  fromNow(date: string | number) {
    const diff = dayjs().diff(dayjs(date), 'days');

    if (diff < 7) {
      return dayjs(date).fromNow().toLowerCase();
    } else if (diff > 7 && diff < 365) {
      return dayjs(date).format('DD MMM');
    } else {
      return dayjs(date).format('DD MMM YY');
    }
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, fromEvent, Observable } from 'rxjs';
import {
  distinctUntilChanged,
  map,
  shareReplay,
  startWith,
  tap,
} from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class DeviceService {
  public isMobile$: Observable<boolean>;

  constructor() {}

  init() {
    this.isMobile$ = fromEvent<any>(window, 'resize').pipe(
      map((event) => {
        return event.target.innerWidth < 768;
      }),
      startWith(window.innerWidth < 768),
      distinctUntilChanged(),
      shareReplay(1)
    );
  }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class HttpService {
  constructor(private http: HttpClient) {}

  get<T>(url: string, params?: object): Observable<T> {
    const httpParams = new HttpParams(params);
    return this.http.get<T>(url, { params: httpParams });
  }
}

import { isPlatformServer } from '@angular/common';
import { PLATFORM_ID } from '@angular/core';
import { Inject, Injectable } from '@angular/core';
import { MatIconRegistry } from '@angular/material/icon';
import { DomSanitizer } from '@angular/platform-browser';
import { environment } from '../../../environments/environment';

@Injectable({
  providedIn: 'root',
})
export class IconRegistryService {
  constructor(
    private iconRegistry: MatIconRegistry,
    private sanitizer: DomSanitizer,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {}

  ICON_NAMES = [
    'option',
    'copy',
    'copy-white',
    'menu',
    'upvote-default',
    'downvote-default',
    'upvote-active',
    'downvote-active',
    'comment',
    'share',
    'purse',
    'qr',
    'close',
    'profile',
    'setting',
    'reply',
    'edit',
    'photo',
    'photo-white',
    'remove',
    'remove-white',
    'coin',
    'option-hor',
    'info',
    'arrow',
    'info-yellow',
  ];

  SOCIAL_ICONS_NAMES = [
    'github',
    'linkedin',
    'medium',
    'telegram',
    'twitter',
    'web',
    'youtube',
    'reddit',
    'facebook',
  ];

  init() {
    let url = 'assets/icons/';
    if (isPlatformServer(this.platformId)) {
      url = environment.domainUrl + url;
    }

    this.registryIcons(url, this.ICON_NAMES);
    this.registryIcons(url + 'links/', this.SOCIAL_ICONS_NAMES);
  }

  private registryIcons(url: string, iconNames: string[]): void {
    for (const name of iconNames) {
      this.iconRegistry.addSvgIcon(
        name + '-icon',
        this.sanitizer.bypassSecurityTrustResourceUrl(url + name + '.svg')
      );
    }
  }
}

