(postId: string) =>
  createSelector(
    selectReplyIdsEntities,
    selectPostEntities,
    selectContentEntities,
    selectProfileEntities,
    selectMyAccountAddress,
    selectMyPostReactionsEntities,
    (
      replyIdEntities,
      postEntities,
      contentEntities,
      profileEntities,
      address,
      myPostReactionsEntities
    ): CommentItemData[] => {
      const commentItemsData: CommentItemData[] = [];

      const replyIds = replyIdEntities[postId];

      if (replyIds) {
        replyIds.replyIds.map((replyPostId) => {
          const postStruct = postEntities[replyPostId];
          const postContent = contentEntities[
            postStruct?.contentId!
          ] as PostContent;
          const profileStruct = profileEntities[postStruct?.ownerId!];
          const profileContent = contentEntities[
            profileStruct?.contentId!
          ] as ProfileContent;
          if (postStruct && postContent) {
            const myPostReaction =
              myPostReactionsEntities[address + '-' + postStruct!.id];

            commentItemsData.push({
              postId: postStruct.id,
              ownerId: postStruct.ownerId,
              profileName: profileContent?.name,
              createdAtTime: postStruct.createdAtTime,
              avatar: profileContent?.avatar,
              commentText: postContent.body,
              upvoteCount: postStruct.upvotesCount,
              downvoteCount: postStruct.downvotesCount,
              replyCount: postStruct.repliesCount,
              upvoteActive: myPostReaction?.kind === 'Upvote',
              downvoteActive: myPostReaction?.kind === 'Downvote',
            });
          }
        });
      }

      return commentItemsData;
    }
  )

(newIds: string[]) =>
  createSelector(selectPostIds, (postIds) => {
    return _.difference(newIds, postIds as string[]);
  })

(ids: string[]) =>
  createSelector(selectPostEntities, (postEntities) => {
    const structs: PostStruct[] = [];
    ids.forEach((id) => {
      const struct = postEntities[id];
      struct ? structs.push(struct) : null;
    });

    return structs;
  })

(ids: string[]) =>
  createSelector(
    selectPostStructByIds(ids),
    selectContentEntities,
    selectProfileEntities,
    selectSpaceEntities,
    selectMyAccountAddress,
    selectMyPostReactionsEntities,
    (
      postArray,
      contentEntities,
      profileEntity,
      spaceEntity,
      address,
      myPostReactionEntities
    ) => {
      const postListItemDataArray: KeyValuePair<PostListItemData> = {};

      postArray.forEach((postStruct) => {
        const profile = profileEntity[postStruct.ownerId];

        const myPostReaction =
          myPostReactionEntities[address + '-' + postStruct.id];

        if (postStruct.contentId) {
          const spaceStruct = postStruct.spaceId
            ? spaceEntity[postStruct.spaceId]
            : undefined;
          const postContent = contentEntities[
            postStruct.contentId
          ] as PostContent;
          const profileContent = profile?.contentId
            ? (contentEntities[profile.contentId] as ProfileContent)
            : undefined;

          const spaceContent = contentEntities[
            spaceStruct?.contentId!
          ] as SpaceContentExtend;

          if (postContent) {
            const postListItemData: PostListItemData =
              mapPostDataToPostListItem(
                postStruct as SharedPostStruct,
                postContent,
                spaceStruct,
                profileContent,
                spaceContent,
                address,
                myPostReaction
              );

            postListItemDataArray[postStruct.id] = postListItemData;
          }
        }
      });

      return postListItemDataArray;
    }
  )

(id: string) =>
  createSelector(
    selectSpaceEntities,
    selectContentEntities,
    (spaceEntities, contentEntities) => {
      const struct = spaceEntities[id];
      if (struct && struct.contentId) {
        const content = contentEntities[struct.contentId] as SpaceContentExtend;
        return content ? ({ struct, content } as SpaceListItemData) : undefined;
      }
      return undefined;
    }
  )

(ids: string[]) =>
  createSelector(
    selectSpaceEntities,
    selectContentEntities,
    (spaceEntities, contentEntities) => {
      const spaceDataArray: KeyValuePair<SpaceListItemData> = {};
      ids.forEach((id) => {
        const struct = spaceEntities[id];
        if (struct?.contentId) {
          const content = contentEntities[
            struct.contentId
          ] as SpaceContentExtend;
          if (content) {
            const spaceListData: SpaceListItemData = {
              struct,
              content,
              id: struct.id,
            };

            spaceDataArray[struct.id] = spaceListData;
          }
        }
      });

      return spaceDataArray;
    }
  )

(start: number, end: number) =>
  createSelector(
    selectAllSpaces,
    selectContentEntities,
    (spacesArray, contentEntities) => {
      const spaceData: SpaceListItemData[] = [];

      spacesArray.slice(start, end).map((struct) => {
        if (struct.contentId) {
          const content = contentEntities[
            struct.contentId
          ] as SpaceContentExtend;
          content ? spaceData.push({ struct, content, id: struct.id }) : null;
        }
      });

      return spaceData;
    }
  )

(ids: string[]) =>
  createSelector(
    selectProfileEntities,
    selectContentEntities,
    (profileEntities, contentEntities) => {
      const usersInfo: KeyValuePair<UserInfo> = {};
      ids.forEach((id) => {
        const profile = profileEntities[id];
        if (profile) {
          let content: ProfileContent | undefined;
          if (profile.contentId) {
            content = contentEntities[profile.contentId] as ProfileContent;
          }

          usersInfo[profile.id] = {
            userName: content?.name,
            address: profile.id,
            avatarSrc: content?.avatar,
            id: profile.id,
            isFollowing: false,
          };
        }
      });

      return usersInfo;
    }
  )

