      replyIdEntities,
      postEntities,
      contentEntities,
      profileEntities,
      address,
      myPostReactionsEntities
    ): CommentItemData[] => {
      const commentItemsData: CommentItemData[] = [];

      const replyIds = replyIdEntities[postId];

      if (replyIds) {
        replyIds.replyIds.map((replyPostId) => {
          const postStruct = postEntities[replyPostId];
          const postContent = contentEntities[
            postStruct?.contentId!
          ] as PostContent;
          const profileStruct = profileEntities[postStruct?.ownerId!];
          const profileContent = contentEntities[
            profileStruct?.contentId!
          ] as ProfileContent;
          if (postStruct && postContent) {
            const myPostReaction =
              myPostReactionsEntities[address + '-' + postStruct!.id];

            commentItemsData.push({
              postId: postStruct.id,
              ownerId: postStruct.ownerId,
              profileName: profileContent?.name,
              createdAtTime: postStruct.createdAtTime,
              avatar: profileContent?.avatar,
              commentText: postContent.body,
              upvoteCount: postStruct.upvotesCount,
              downvoteCount: postStruct.downvotesCount,
              replyCount: postStruct.repliesCount,
              upvoteActive: myPostReaction?.kind === 'Upvote',
              downvoteActive: myPostReaction?.kind === 'Downvote',
            });
          }
        });
      }

      return commentItemsData;
    }
  )

src/app/state/space/space.selectors.ts.defaultValue:
(id: string) =>
  createSelector(
    selectSpaceEntities,
    selectContentEntities,
    (spaceEntities, contentEntities) => {
      const struct = spaceEntities[id];
      if (struct && struct.contentId) {
        const content = contentEntities[struct.contentId] as SpaceContentExtend;
        return content ? ({ struct, content } as SpaceListItemData) : undefined;
      }
      return undefined;
    }
  )

(ids: string[]) =>
  createSelector(
    selectSpaceEntities,
    selectContentEntities,
    (spaceEntities, contentEntities) => {
      const spaceDataArray: KeyValuePair<SpaceListItemData> = {};
      ids.forEach((id) => {
        const struct = spaceEntities[id];
        if (struct?.contentId) {
          const content = contentEntities[
            struct.contentId
          ] as SpaceContentExtend;
          if (content) {
            const spaceListData: SpaceListItemData = {
              struct,
              content,
              id: struct.id,
            };

            spaceDataArray[struct.id] = spaceListData;
          }
        }
      });

      return spaceDataArray;
    }
  )

(start: number, end: number) =>
  createSelector(
    selectAllSpaces,
    selectContentEntities,
    (spacesArray, contentEntities) => {
      const spaceData: SpaceListItemData[] = [];

      spacesArray.slice(start, end).map((struct) => {
        if (struct.contentId) {
          const content = contentEntities[
            struct.contentId
          ] as SpaceContentExtend;
          content ? spaceData.push({ struct, content, id: struct.id }) : null;
        }
      });

      return spaceData;
    }
  )

